mapper.update:
update org_info
set status = 'disabled', update_at = CURRENT_TIMESTAMP()
where identity_id = #{identityId,jdbcType=VARCHAR}

update org_info
set status = #{status,jdbcType=INTEGER}, update_at = CURRENT_TIMESTAMP()
where identity_id = #{identityId,jdbcType=VARCHAR}

mapper.insert:
insert into org_info (identity_id, identity_type, node_id, org_name, status,
  accumulative_data_file_count, update_at
  )
values (#{identityId,jdbcType=VARCHAR}, #{identityType,jdbcType=VARCHAR}, #{nodeId,jdbcType=VARCHAR}, #{orgName,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER},
   #{accumulativeDataFileCount,jdbcType=INTEGER}, CURRENT_TIMESTAMP()
  )

mapper.select:
select

from org_info
where update_at  >  #{lastUpdatedAt,jdbcType=TIMESTAMP}
order by update_at
limit 0, #{limit,jdbcType=BIGINT}
