mapper.update:
update org_info
set status = 'disabled'
where identity_id = #{identityId,jdbcType=VARCHAR}

mapper.insert:
insert into org_info (identity_id, identity_type, org_name, status,
  accumulative_memory, accumulative_core, accumulative_Bandwidth, accumulative_power_task_count, accumulative_data_task_count, accumulative_data_file_count
  )
values (#{identityId,jdbcType=VARCHAR}, #{identityType,jdbcType=VARCHAR}, #{orgName,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR},
  #{accumulativeMemory,jdbcType=BIGINT}, #{accumulativeCore,jdbcType=INTEGER}, #{accumulativeBandwidth,jdbcType=BIGINT},
  #{accumulativePowerTaskCount,jdbcType=INTEGER} , #{accumulativeDataTaskCount,jdbcType=INTEGER}, #{accumulativeDataFileCount,jdbcType=INTEGER}
  )
