mapper.update:
update org_info
set status = 'disabled'
where identity_id = #{identityId,jdbcType=VARCHAR}

update org_info
set status = #{status,jdbcType=INTEGER}
where identity_id = #{identityId,jdbcType=VARCHAR}

mapper.insert:
insert into org_info (identity_id, identity_type, node_id, org_name, status, image_url, profile,
  accumulative_data_file_count
  )
values (#{identityId,jdbcType=VARCHAR}, #{identityType,jdbcType=VARCHAR}, #{nodeId,jdbcType=VARCHAR}, #{orgName,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{imageUrl,jdbcType=VARCHAR}, #{profile,jdbcType=VARCHAR},
   #{accumulativeDataFileCount,jdbcType=INTEGER}
  )

insert into org_info (identity_id, identity_type, node_id, org_name, status, image_url, profile,
  accumulative_data_file_count
  )
values

  (#{item.identityId,jdbcType=VARCHAR}, #{item.identityType,jdbcType=VARCHAR}, #{item.nodeId,jdbcType=VARCHAR}, #{item.orgName,jdbcType=VARCHAR}, #{item.status,jdbcType=INTEGER}, #{item.imageUrl,jdbcType=VARCHAR}, #{item.profile,jdbcType=VARCHAR},
    #{item.accumulativeDataFileCount,jdbcType=INTEGER}
    )

mapper.select:
select

from org_info
where update_at  >  #{lastUpdatedAt,jdbcType=TIMESTAMP}
order by update_at, identity_id
limit 0, #{limit,jdbcType=BIGINT}
