mapper.delete:
delete from task_meta_data_column
where task_id = #{taskId,jdbcType=VARCHAR}
  and meta_data_id = #{metaDataId,jdbcType=VARCHAR}
  and selected_column_idx = #{selectedColumnIdx,jdbcType=INTEGER}

mapper.insert:
insert into task_meta_data_column (task_id, meta_data_id, selected_column_idx
  )
values (#{taskId,jdbcType=VARCHAR}, #{metaDataId,jdbcType=VARCHAR}, #{selectedColumnIdx,jdbcType=INTEGER}
  )

insert into task_meta_data_column (task_id, meta_data_id, selected_column_idx
)
values

  (#{taskMetaDataColumn.taskId,jdbcType=VARCHAR}, #{taskMetaDataColumn.metaDataId,jdbcType=VARCHAR}, #{taskMetaDataColumn.selectedColumnIdx,jdbcType=INTEGER}
  )

mapper.select:
select task_id, meta_data_id, selected_column_idx
from task_meta_data_column
where task_id = #{taskId,jdbcType=VARCHAR} and meta_data_id = #{metaDataId,jdbcType=VARCHAR}
